package com.h2h.test.it;

import com.h2h.pda.pojo.SettingParam;
import org.assertj.core.api.AssertionsForClassTypes;
import org.junit.jupiter.api.Order;
import org.junit.jupiter.api.Test;
import org.springframework.http.HttpMethod;
import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;

import java.util.ArrayList;
import java.util.List;

import static org.assertj.core.api.Assertions.assertThat;

public class SettingsTagAndStatisticsControllerTest extends BaseIntegrationTests{
    final String url = "/api/v1/tag/";
    @Test
    @Order(450)
    public void SettingTagsValues(){
        loginWithDefaultUserToken();
        List<SettingParam> settingParams = new ArrayList<>();
        settingParams.add(new SettingParam("app_login_page_logo", "data:image/png;base64,"));
        settingParams.add(new SettingParam("app_login_page_message","##### H2HSecure\n``` Welcome PDAccess Desktop App 3```"));
        settingParams.add(new SettingParam("login_page_logo","data:image/png;base64,"));
        settingParams.add(new SettingParam("login_page_message","#### H2HSecure"));
        settingParams.add(new SettingParam("navbar_logo","data:image/png;base64,"));
        settingParams.add(new SettingParam("after_sign_out_path","test"));
        settingParams.add(new SettingParam("home_page_url","/activities/nav/livesessions?sort=createddesc"));
        settingParams.add(new SettingParam("two_factor_auth","1"));


        ResponseEntity<Void> setSettings = call("/api/v1/settings", HttpMethod.POST, settingParams, Void.class);
        AssertionsForClassTypes.assertThat(setSettings.getStatusCode()).isEqualTo(HttpStatus.OK);

        ResponseEntity<String> call = call(url + "login-page-message", HttpMethod.GET, String.class);
        assertThat(call.getStatusCode()).isEqualTo(HttpStatus.OK);
        assertThat(call.getBody()).isEqualTo(settingParams.get(3).getValue());

        call = call(url + "app-login-page-message", HttpMethod.GET, String.class);
        assertThat(call.getStatusCode()).isEqualTo(HttpStatus.OK);
        assertThat(call.getBody()).isEqualTo(settingParams.get(1).getValue());

        call = call(url + "login-page-image", HttpMethod.GET, String.class);
        assertThat(call.getStatusCode()).isEqualTo(HttpStatus.OK);
        assertThat(call.getBody()).isEqualTo(settingParams.get(2).getValue());

        call = call(url + "login-page-message", HttpMethod.GET, String.class);
        assertThat(call.getStatusCode()).isEqualTo(HttpStatus.OK);
        assertThat(call.getBody()).isEqualTo(settingParams.get(3).getValue());

        call = call(url + "navbar-logo", HttpMethod.GET, String.class);
        assertThat(call.getStatusCode()).isEqualTo(HttpStatus.OK);
        assertThat(call.getBody()).isEqualTo(settingParams.get(4).getValue());

        call = call(url + "sign-out-path", HttpMethod.GET, String.class);
        assertThat(call.getStatusCode()).isEqualTo(HttpStatus.OK);
        assertThat(call.getBody()).isEqualTo(settingParams.get(5).getValue());

        call = call(url + "home-page-url", HttpMethod.GET, String.class);
        assertThat(call.getStatusCode()).isEqualTo(HttpStatus.OK);
        assertThat(call.getBody()).isEqualTo(settingParams.get(6).getValue());

        call = call(url + "mfa-status", HttpMethod.GET, String.class);
        assertThat(call.getStatusCode()).isEqualTo(HttpStatus.OK);
        assertThat(call.getBody()).isEqualTo(settingParams.get(7).getValue());
    }

    @Test
    @Order(451)
    public void getStatistics(){
        loginWithDefaultUserToken();
        ResponseEntity<String> call = call("/api/v1/admin/statistics", HttpMethod.GET, String.class);
        assertThat(call.getStatusCode()).isEqualTo(HttpStatus.OK);
    }
}
